mapper.insert:
SELECT
	nextval('account.mutation_id_seq')

INSERT INTO mutation(
id
, accountnumber
, currency
, interestdate
, balancebefore
, balanceafter
, transactiondate
, amount
, description
)
VALUES(
#{id}
, #{accountNumber}
, #{currency}
, #{interestDate}
,
#{balanceBefore}
, #{balanceAfter}
, #{transactionDate}
, #{amount}
, #{description}
);

mapper.update:
UPDATE mutation
SET
ordernumber = #{ordernumber}
WHERE
id = #{id}
